from settings import settings
from handlers.buttons import commands as cmd, menu as btn


def instruction_message() -> str:
    """–ò–Ω—Å—Ç—Ä—É–∫—Ü–∏—è"""
    message = "–í—ã–±–µ—Ä–∏—Ç–µ —Ç–∏–ø –æ–ø–µ—Ä–∞—Ü–∏–æ–Ω–Ω–æ–π —Å–∏—Å—Ç–µ–º—ã —É—Å—Ç—Ä–æ–π—Å—Ç–≤–∞ —Å –ø–æ–º–æ—â—å—é –∫–Ω–æ–ø–æ–∫ –Ω–∏–∂–µ"
    return message


def help_message() -> str:
    """Help —Å–æ–æ–±—â–µ–Ω–∏–µ"""
    message = f"–î–ª—è –¥–æ—Å—Ç—É–ø–∞ –∫ VPN, –≤–∞–º –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ –∫—É–ø–∏—Ç—å VPN –∫–ª—é—á üîë –≤ —Ä–∞–∑–¥–µ–ª–µ /{cmd.BUY[0]}, –Ω–æ–≤—ã–º –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è–º –≤—ã–¥–∞–µ—Ç—Å—è –ø—Ä–æ–±–Ω—ã–π –∫–ª—é—á (—Å—Ä–æ–∫–æ–º –Ω–∞ 1 –¥–µ–Ω—å), –æ–Ω –±—É–¥–µ—Ç –æ—Ç–æ–±—Ä–∞–∂–µ–Ω –≤ –≤–∞—à–∏—Ö –∫–ª—é—á–∞—Ö –≤ —Ä–∞–∑–¥–µ–ª–µ /{cmd.KEYS[0]} –≤ –ø–µ—Ä–∏–æ–¥ –µ–≥–æ –¥–µ–π—Å—Ç–≤–∏—è\n\n" \
              f"<b>üí∞–ë–∞–ª–∞–Ω—Å</b>\n–ü–æ–ø–æ–ª–Ω–∏—Ç—å –±–∞–ª–∞–Ω—Å –≤—ã –º–æ–∂–µ—Ç–µ –≤ —Ä–∞–∑–¥–µ–ª–µ /{cmd.BALANCE[0]} –¥–≤—É–º—è —Å–ø–æ—Å–æ–±–∞–º–∏:\n" \
              f"- –ø–µ—Ä–µ–≤–æ–¥–æ–º –ø–æ –Ω–æ–º–µ—Ä—É –∫–∞—Ä—Ç—ã\n- –æ–ø–ª–∞—Ç–∏—Ç—å –±–∞–Ω–∫–æ–≤—Å–∫–æ–π –∫–∞—Ä—Ç–æ–π —á–µ—Ä–µ–∑ –ø–ª–∞—Ç–µ–∂–Ω—ã–π —Å–µ—Ä–≤–∏—Å\n\n" \
              f"<b>üí≤ –£—Å–ª–æ–≤–∏—è –∏ —Ü–µ–Ω—ã</b>\n–°—Ç–æ–∏–º–æ—Å—Ç—å –∫–ª—é—á–∞ –Ω–∞ {settings.paid_period} –¥–Ω–µ–π ({settings.traffic_limit} –ì–ë) —Å–æ—Å—Ç–∞–≤–ª—è–µ—Ç {settings.price_list['1']} —Ä. (–≤—Å–µ —Ç–∞—Ä–∏—Ñ—ã –≤—ã –º–æ–∂–µ—Ç–µ –ø–æ—Å–º–æ—Ç—Ä–µ—Ç—å –≤ —Ä–∞–∑–¥–µ–ª–µ /{cmd.BUY[0]})\n\n" \
              f"<b>üîë –ö–ª—é—á–∏</b>\n–ü–æ–ª—É—á–∏—Ç—å –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ —Å–≤–æ–∏—Ö –∫–ª—é—á–∞—Ö (–¥–∞—Ç—É –æ–∫–æ–Ω—á–∞–Ω–∏—è –æ–ø–ª–∞—á–µ–Ω–Ω–æ–≥–æ –ø–µ—Ä–∏–æ–¥–∞, –æ–±—ä–µ–º –ø–æ—Ç—Ä–µ–±–ª–µ–Ω–Ω–æ–≥–æ —Ç—Ä–∞—Ñ–∏–∫–∞, —Å—Ç—Ä–∞–Ω—É –∏ —Å–∞–º –∫–ª—é—á), –∞ —Ç–∞–∫–∂–µ —É–¥–∞–ª–∏—Ç—å –∫–ª—é—á \"{btn.DELETE_KEY}\" –≤—ã –º–æ–∂–µ—Ç–µ –≤ —Ä–∞–∑–¥–µ–ª–µ /{cmd.KEYS[0]}\n\n" \
              f"<b>üóìÔ∏è –ü—Ä–æ–¥–ª–µ–Ω–∏–µ –∫–ª—é—á–µ–π</b>\n–í —Ä–∞–∑–¥–µ–ª–µ /{cmd.BUY[0]} –≤—ã –º–æ–∂–µ—Ç–µ –ø—Ä–æ–¥–ª–∏—Ç—å —Å—Ä–æ–∫ –¥–µ–π—Å—Ç–≤–∏—è –≤—ã–±—Ä–∞–Ω–Ω–æ–≥–æ –∫–ª—é—á–∞ \"{btn.EXTEND_KEY}\" (–∫—É–ø–ª–µ–Ω–Ω—ã–π —Å—Ä–æ–∫ –¥–µ–π—Å—Ç–≤–∏—è –∫–ª—é—á–∞ –±—É–¥–µ—Ç –¥–æ–±–∞–≤–ª–µ–Ω –∫ —Ç–µ–∫—É—â–µ–º—É) –∏–ª–∏ –∫—É–ø–∏—Ç—å –Ω–æ–≤—ã–π \"{btn.NEW_KEY}\"\n\n" \
              f"<b>üìä –¢—Ä–∞—Ñ–∏–∫</b>\n–í –º–µ—Å—è—Ü –Ω–∞ –∫–∞–∂–¥—ã–π –∫–ª—é—á –≤—ã–¥–µ–ª—è–µ—Ç—Å—è {settings.traffic_limit} –ì–ë —Ç—Ä–∞—Ñ–∏–∫–∞, –∫–æ—Ç–æ—Ä—ã–π –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ –æ–±–Ω–æ–≤–ª—è–µ—Ç—Å—è –∫–∞–∂–¥—ã–µ {settings.paid_period} –¥–Ω–µ–π. " \
              f"–í —Å–ª—É—á–∞–µ –ø—Ä–µ–≤—ã—à–µ–Ω–∏—è –ª–∏–º–∏—Ç–∞ —Ç—Ä–∞—Ñ–∏–∫–∞, –∫–ª—é—á –±—É–¥–µ—Ç –∑–∞–±–ª–æ–∫–∏—Ä–æ–≤–∞–Ω.\n–í—ã –º–æ–∂–µ—Ç–µ –æ–±–Ω–æ–≤–∏—Ç—å —Ç—Ä–∞—Ñ–∏–∫ –ª—é–±–æ–≥–æ –∫–ª—é—á–∞ (—Ç–µ–∫—É—â–∏–π —Ç—Ä–∞—Ñ–∏–∫ –≤—ã–±—Ä–∞–Ω–Ω–æ–≥–æ –∫–ª—é—á–∞ –±—É–¥–µ—Ç –æ–±–Ω—É–ª–µ–Ω) –≤ —Ä–∞–∑–¥–µ–ª–µ /{cmd.KEYS[0]} —Å –ø–æ–º–æ—â—å—é –∫–Ω–æ–ø–∫–∏ \"{btn.EXTRA_TRAFFIC}\", –ª–∏–±–æ –¥–æ–∂–¥–∞—Ç—å—Å—è –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–æ–≥–æ –µ–∂–µ–º–µ—Å—è—á–Ω–æ–≥–æ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è —Ç—Ä–∞—Ñ–∏–∫–∞." \
              f"\n–°—Ç–æ–∏–º–æ—Å—Ç—å –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è —Ç—Ä–∞—Ñ–∏–∫–∞ - {settings.extra_traffic_price} —Ä. –¥–ª—è –æ–¥–Ω–æ–≥–æ –∫–ª—é—á–∞\n\n" \
              f"<b>üí° –£—Å—Ç–∞–Ω–æ–≤–∫–∞ –∏ –Ω–∞—Å—Ç—Ä–æ–π–∫–∞</b>\n–ü–æ–¥—Ä–æ–±–Ω—É—é –∏–Ω—Å—Ç—Ä—É–∫—Ü–∏—é –ø–æ —É—Å—Ç–∞–Ω–æ–≤–∫–µ –∏ –Ω–∞—Å—Ç—Ä–æ–π–∫–µ VPN –¥–ª—è –≤–∞—à–µ–≥–æ —É—Å—Ç—Ä–æ–π—Å—Ç–≤–∞ –≤—ã –º–æ–∂–µ—Ç–µ –ø–æ—Å–º–æ—Ç—Ä–µ—Ç—å –≤ —Ä–∞–∑–¥–µ–ª–µ /{cmd.INSTRUCTION[0]}"


    message += f"\n\n<b>‚ùì –í–æ–ø—Ä–æ—Å—ã</b>\n–ü–æ –ª—é–±—ã–º –≤–æ–∑–Ω–∏–∫—à–∏–º –≤–æ–ø—Ä–æ—Å–∞–º –∏ —Ç–µ—Ö–Ω–∏—á–µ—Å–∫–∏–º –ø—Ä–æ–±–ª–µ–º–∞–º –≤—ã –º–æ–∂–µ—Ç–µ –æ–±—Ä–∞—Ç–∏—Ç—å—Å—è –∫ –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä—É @{settings.help_admin}"

    return message
